import React,{useState,useEffect} from 'react';
import axios from 'axios';
import './Task_css.css';


function Main() {
	 const [lyricsdata,setLyricsdata]=useState([]);
	 const [searchterm,setSearchterm]=useState("");


	useEffect(() => {
    const fetchData = async () => {
      const result = await axios("https://cors-access-allow.herokuapp.com/http://api.musixmatch.com/ws/1.1/track.search?q_artist=justinbieber&page_size=3&page=1&s_track_rating=desc&apikey=${process.env.REACT_APP_MM_KEY}");
      console.log(result);
	  setLyricsdata(result.data.message.body.track_list);
    };

    fetchData();
  },[]);


  return (
  <>
	  <div className="container header">
	  <input 
				type="text"
                id="text1"				
				placeholder="SEARCH LYRICS"
				value={searchterm}
				onChange={(event)=>{setSearchterm(event.target.value)}}/>

	  </div>

	<div className="container header1">
	{lyricsdata &&lyricsdata.length>0 
	?lyricsdata.map((item=>{
		console.log(lyricsdata);
		return(<h1 key={item.lyrics_id}>{item.lyrics_body}</h1>


	);

	}))
	:"loading...."
	}
	</div>
  </>
  );
}

export default Main;
