import React,{useState,useEffect} from 'react';
import Data from './Data';
import './Main_css.css';



function Main() {
	const [items,setItems]=useState(Data);
	const [flipcheck,setFlipcheck]=useState(false);
	const [shoecheck,setShoecheck]=useState(false);
	const filteritem=(category)=>{
		const updateditems=items.filter((elem)=>{
			return elem.category===category;
		});
		setItems(updateditems);
	}
return (
  <>
  <div className="row">		
	<div className="column left">
	 <h1>CATEGORIES</h1>
	  <div className="check">
			 <input type="checkbox" checked={flipcheck} onChange={(e)=>setFlipcheck(e.target.checked)}/> Flip Flops <br/>
			 <input type="checkbox" /> Sneakers   <br/>
			 <input type="checkbox"  /> Lace-Up shoes <br/>
			 <input type="checkbox" /> Shoe Accessories <br/>
	 </div>
	 <hr/>
	 <h1>SIZE</h1>
		 <div className="gridcontainer">
		  <button type="button" onClick={filteritem(34)}>34</button>
		  <button type="button">35</button>
		  <button type="button">36</button>
		  <button type="button">37</button> <br/><br/>
		  <button type="button">38</button>
		  <button type="button">39</button>
		  <button type="button">40</button>
		  <button type="button">41</button>
		 </div>
	 <hr/>
	<h1>Price Range</h1>
	<input type="range" id="vol" name="vol" min="500" max="2000"/>

	</div>

	<div className="column right">
  {items.map((elem=>{
	  if( flipcheck && elem.category === 'flipflop'){
	   return(
			  <>

				<div className="grid-container">

					<img key={elem.id} src={elem.img}/>
					<h3 key={elem.id}>Price:- {elem.price}</h3>

				</div>

			</>
  );
}

if(shoecheck & elem.category === 'shoes'){
	   return(
			  <>

				<div className="grid-container">

					<img key={elem.id} src={elem.img}/>
					<h3 key={elem.id}>Price:- {elem.price}</h3>

				</div>

			</>
  );
}
else{
return(
			  <>

				<div className="grid-container">

					<img key={elem.id} src={elem.img}/>
					<h3 key={elem.id}>Price:- {elem.price}</h3>

				</div>

			</>
  );	
}








  }))}


  </div>
  </div>
  </>
  );
}

export default Main;
