import React,{useState,useEffect} from 'react';
import './Task_css.css';
import { AiFillHome } from 'react-icons/ai';
import Main from './Main';



function Header1() {
	const [searchterm,setSearchterm]=useState(null);
	const [moviedata,setMoviedata]=useState([]);
	const [output,setOutput]=useState([]);


	const url="https://api.themoviedb.org/3/movie/upcoming?api_key=a0d1ccffb7077d4acb8d946b535565ac&language=en-US&page=1";

	useEffect(() => {
    loadData();
	},[searchterm]);





  const loadData=async()=>{
	  const response=await fetch(url);
	  const data=await response.json();
	  setMoviedata(data.results);
	  console.log(data);
  }


  return (
  <>

		 <div className="container header">
			<div className="merge">	
				<input 
				type="text"
                id="text1"				
				placeholder="SEARCH"
				value={searchterm}
				onChange={(event)=>{setSearchterm(event.target.value)}}/>


				<h4><AiFillHome/></h4>
			</div>


		 </div>


		{moviedata.map((item,key)=>{
			console.log("search is:"+searchterm);
			console.log("search is:"+ item.original_title)
			if(item.original_title===searchterm)
			{
				return (
				<>
				<div className="card" >
					<img src="https://upload.wikimedia.org/wikipedia/en/c/c5/Clifford2021Poster.jpg" className="card-img-top" alt="not loading"/>
					  <div className="card-body">
						<h5 className="card-title" key={item.id}>{item.original_title}</h5>
						<p className="card-text" key={item.id} >{item.overview}</p>
						<a href="#" className="btn btn-primary">Go somewhere</a>
					  </div>
                </div>

				</>
				);
			}
			else{
				return <Main/>
			}

		})}

	 </>
	);



}

export default Header1;
