	const handlesubmit=(event)=>{
		event.preventDefault();
		alert("Login is successful");
	}
  return (
  <>
  <form onSubmit={handlesubmit}>
 <div className="login">
 <h1 className=" mb-5 text-center text-primary">LOGIN FORM</h1>
   <div class="mb-3 row justify-content-center ">
    <label for="staticEmail" class="col-sm-2 col-form-label">Email</label>
    <div class="col-sm-6">
      <input type="email" class="form-control" id="exampleFormControlInput1" value={loginemail} onChange={(e)=>setEmail(e.target.value)} placeholder="Enter Email" required/>
    </div>
  </div>

  <div class="mb-3 row justify-content-center">
    <label for="inputPassword" class="col-sm-2 col-form-label">Password</label>
    <div class="col-sm-6">
      <input type="password" class="form-control" id="inputPassword" value={loginpassword} onChange={(e)=>setPassword(e.target.value)} placeholder="Enter password" required/>
    </div>
    </div>
	<p className="p1">Forgot password?</p>




 <button type="submit" id="btn2"  target="_blank" onClick={()=>setLogin(true)}>Login</button>
 <p className="p2">Not a member? Signup now</p>
 </div>
 {login && <List/>}
</form>
  </>
  );
}

export default Login;
