import React,{useState} from 'react';
import Data from './Data';
import Header from './Header';
import './Shopping_css.css';
import Footer from './Footer';

function Main1(){
	const [data1,setdata1]=useState(Data);
	const [cart,setCart]=useState(0);
	return(
	<>
	<Header cart={cart}/>
	<div className="main2">
	<h1 class="text-center text-success">- KURTIS -</h1>
	{data1.map((item=>{
		return(
		<>
		<div class="container">
		<div class="card border-primary ">
		<img key={item.id} src={item.img} className="img2" height="250px" width="273px"/>
	    <div class="card-body">
		<h5 key={item.id} class="card-title">{item.title}</h5>
		<p key={item.id} class="card-text">{item.price}</p>
		<button href="#" class="btn btn-primary" onClick={()=>setCart(cart+1)}>ADD TO CART</button>
		</div>
		</div>
	    </div>
		</>
		);
	}))}
	<button type="button" class="btn btn-danger" onClick={()=>setCart(0)} >CLEAR CART</button>
</div>
<Footer/>
	</>
	);
}

export default Main1;
