import React,{useState,useEffect} from 'react';
import './Task_css.css';
import { AiFillHome } from 'react-icons/ai';
import Main from './Main';
import Moviedetails from './Moviedetails';



function Header1() {
	const [searchterm,setSearchterm]=useState(null);
	const [moviedata,setMoviedata]=useState([]);
	const [active,setActive]=useState("");



	const url="https://api.themoviedb.org/3/movie/upcoming?api_key=a0d1ccffb7077d4acb8d946b535565ac&language=en-US&page=1";

	useEffect(() => {
    loadData();
	},[searchterm]);





  const loadData=async()=>{
	  const response=await fetch(url);
	  const data=await response.json();
	  setMoviedata(data.results);
	  console.log(data);
  }


  return (
  <>

		 <div className="container header">
			<div className="merge">	
				<input 
				type="text"
                id="text1"				
				placeholder="SEARCH BY TITLE"
				value={searchterm}
				onChange={(event)=>{setSearchterm(event.target.value)}}/>


				<h4><AiFillHome/></h4>
			</div>


		 </div>


		{moviedata.map((item,key)=>{
			console.log("search is:"+searchterm);
			console.log("search is:"+ item.original_title)
			let text1=item.overview;
		   let desc=text1.slice(0,160);
			if(item.original_title===searchterm)
			{
				return (
				<>
				<div className="card" id="onecard">

		{item.id===610253 &&  <img key={item.id} src="https://m.media-amazon.com/images/M/MV5BM2RmMGY2Y2UtNjA1NS00NGE4LThiNzItMmE1NTk5NzI5NmE0XkEyXkFqcGdeQXVyNjY1MTg4Mzc@._V1_FMjpg_UX1000_.jpg" class="card-img-top" alt="not laoding..." height="250"/>}
		 {item.id===646385  && <img key={item.id} src="https://c8.alamy.com/comp/E5M8AF/scream-us-poster-art-1996-dimension-film-courtesy-everett-collection-E5M8AF.jpg" alt="notgood" height="250"   />}
		 {item.id=== 585245 && <img key={item.id} src="https://upload.wikimedia.org/wikipedia/en/c/c5/Clifford2021Poster.jpg"alt="notgood" height="250"   />}  
		 {item.id===438695 && <img  key={item.id} src="https://assets.gadgets360cdn.com/pricee/assets/product/202112/MV5BOTgzYzA3NWItYzkxZC00OWQ2LTkx_1640240928.jpg" alt="notgood" height="250"  />}
		 {item.id===335787  && <img  key={item.id} src="https://m.media-amazon.com/images/M/MV5BYzM5NTFlNjEtZDU5Ni00N2Y4LWE0YWUtMjMwN2Y2MTk5YjBkXkEyXkFqcGdeQXVyMTMwMTQyMDI5._V1_.jpg" alt="notgood" height="250"  />}
		 {item.id===522016 && <img   key={item.id} src="https://m.media-amazon.com/images/M/MV5BNjRjMjk1MTItZGRjOC00NDhmLWExMTItY2Y4YTI2MGNmNTlkXkEyXkFqcGdeQXVyMTA3MDk2NDg2._V1_.jpg" alt="notgood" height="250"  />}
		{item.id===588921 && <img   key={item.id} src="https://cartoonimages.club/wp-content/uploads/2021/02/Ainbo-Spirit-Of-Amazon-cartoonimages-club-Zumi-448x560.jpg" alt="notgood" height="250"  />}
		{item.id===597208  && <img   key={item.id} src="https://m.media-amazon.com/images/M/MV5BOTI4NDhhNGEtZjQxZC00ZTRmLThmZTctOGJmY2ZlOTc0ZGY0XkEyXkFqcGdeQXVyMTkxNjUyNQ@@._V1_FMjpg_UX1000_.jpg" alt="notgood" height="250"  />}
		{item.id=== 554594 && <img   key={item.id} src="https://images.fandango.com/ImageRenderer/0/0/redesign/static/img/default_poster.png/0/images/masterrepository/Fandango/225848/KOATI_Poster_Dated_ENG_4050x6000.jpg" alt="notgood" height="250"  />}
        {item.id=== 614917 && <img   key={item.id} src="https://besttoppers.com/wp-content/uploads/2020/12/king-richard.jpg" alt="notgood" height="250"  />}
		{item.id===716612  && <img   key={item.id} src="https://mustangpublications.com/wp-content/uploads/2021/12/spencer_movie_poster_by_szerina_ded8ib3-fullview.jpg" alt="notgood" height="250"  />}
		{item.id===406759 && <img   key={item.id} src="https://thestudioexec.com/wp-content/uploads/2022/01/moonfall.jpg" alt="notgood" height="250"  />}
		{item.id=== 655431 && <img   key={item.id} src="https://i.pinimg.com/736x/3e/f5/b3/3ef5b3eee66ba0e7f8e9d5a6d6ef2398.jpg" alt="notgood" height="250"  />}
		{item.id===615904 && <img   key={item.id} src="https://i.pinimg.com/736x/46/ec/54/46ec54306c411d787877609c350625ef.jpg" alt="notgood" height="250"  />}
		{item.id===454527  && <img   key={item.id} src="http://mcswebsites.blob.core.windows.net/1033/Event_10061/portrait_large/Benedetta_1080x1920px_DigitalPoster.jpg" alt="notgood" height="250"  />}
		{item.id===656663  && <img   key={item.id} src="https://m.media-amazon.com/images/M/MV5BOTgwMmU0YzktOGNhNi00MDcyLTg1OGEtZGQwM2RlMTAyYzhlXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_FMjpg_UX1000_.jpg" alt="notgood" height="250"  />}
		{item.id===776503  && <img   key={item.id} src="https://www.apple.com/tv-pr/articles/2021/12/apple-original-films-coda-honored-with-nine-hollywood-critics-association-film-award-nominations-including-best-picture/images/big-image/big-image-01/120221_CODA_Honored_Nine_Award_Nominations_Big_Image_01_big_image_post.jpg.large.jpg" alt="notgood" height="250"  />}
		{item.id===554230 && <img   key={item.id} src="http://gonewiththetwins.com/new/wp-content/uploads/2021/12/lostdaughter.jpg" alt="notgood" height="250"  />}
		{item.id===810693  && <img   key={item.id} src="https://realsound.jp/wp-content/uploads/2021/07/20210725-jyujyu-468x468.jpg" alt="notgood" height="250"  />}
		{item.id===573699  && <img   key={item.id} src="https://img4.km.com/dianyingimg/characterimg/204148/201909281524056405758.jpg" alt="notgood" height="250"  />}
		{item.id===644495  && <img   key={item.id} src="https://upload.wikimedia.org/wikipedia/en/f/fe/House.of.Gucci.film.jpeg" alt="notgood" height="250"  />}	
		{item.id===766798  && <img   key={item.id} src="https://timesread.com/wp-content/uploads/2021/08/Madres-Paralelas.jpg" alt="notgood" height="250"  />}	



					  <div className="card-body">
						<h3 className="card-title" key={item.id}>{item.original_title}</h3>
						<h4 className="card-text" key={item.id} >{item.vote_average}</h4>
						<p className="card-text" key={item.id} >{desc}</p>
						<button type="button" className="bg-primary" key={item.id} onClick={()=>setActive(item.id)} target="_blank">Movie Details</button>
		  {active===item.id && <Moviedetails id={item.id} title={item.original_title} desc={desc}/>}

					  </div>
                </div>

				</>
				);
			}

		})}
  <Main/>
	 </>
	);



}

export default Header1;
