import React,{useState,useEffect} from 'react';
import Data from './Data';
import DataF from './DataF';
import DataS from './DataS';
import './Main_css.css';



function Main() {
	const [items,setItems]=useState(Data);
	const [flipcheck,setFlipcheck]=useState(false);
	const [shoecheck,setShoecheck]=useState(false);
	const [allcheck,setAllcheck]=useState(true);

	const filteritem=(size)=>{
	const updateditems=DataF.filter((elem)=>{
	return elem.size===size;
	});
	setItems(updateditems);
	};

	const checkitem2=(categ)=>{
	const updateditems=DataS.filter((elem)=>{
	return elem.category===categ;
	});
	setItems(updateditems);
	setFlipcheck(false);
	};

	const checkitem1=(categ)=>{
	const updateditems=DataF.filter((elem)=>{
	return elem.category===categ;
	});
	setItems(updateditems);
	setShoecheck(false);
	};

return (
  <>
  <div className="row">		
	<div className="column left">
	 <h1>CATEGORIES</h1>
	  <div className="check">
			 <input type="checkbox" name="flip1" checked={flipcheck} onChange={(e)=>setFlipcheck(e.target.checked)} onClick={()=>checkitem1("flipflop")}/> Flip Flops <br/>
			 <input type="checkbox"  checked={shoecheck} onChange={(e)=>setFlipcheck(e.target.checked)} onClick={()=>checkitem2("laceup")}/> Lace-Up shoes <br/>

	 </div>
	 <hr/>
	 <h1>SIZE</h1>
		 <div className="gridcontainer">
		  <button type="button" onClick={()=>filteritem(34)} >34</button>
		  <button type="button" onClick={()=>filteritem(35)}>35</button>
		  <button type="button" onClick={()=>filteritem(36)}>36</button>
		  <button type="button" onClick={()=>filteritem(37)}>37</button> <br/><br/>
		  <button type="button" onClick={()=>filteritem(38)}>38</button>
		  <button type="button" onClick={()=>filteritem(39)}>39</button>
		  <button type="button" onClick={()=>filteritem(40)}>40</button>
		  <button type="button" onClick={()=>filteritem(41)}>41</button>
		 </div>
	 <hr/>
	<h1>Price Range</h1>
	<input type="range" id="vol" name="vol" min="500" max="2000"/>

	</div>

	<div className="column right">
  {items.map((elem=>{

	  return(
	  <>


				<div className="grid-container">

					<img key={elem.id} src={elem.img}/>
					<h5 key={elem.id}>Price:- {elem.price}</h5>

				</div>




	</>	
);
  }))}


  </div>
  </div>
  </>
  );
}

export default Main;
