import React,{useState} from 'react';
import './Task_css.css';


function Signup(){
	const [email,setEmail]=useState("");
	const [password,setPassword]=useState("");
	const [confirmPassword,setCpassword]=useState("");


	const handlestore=()=>{
		localStorage.setItem("email",email);
		localStorage.setItem("password",password);
		localStorage.setItem("email",email);

	}
  return (
  <>
 <div className="container">
 <h1 className=" mb-5 text-center text-primary">SIGN UP</h1>
   <div class="mb-3 row justify-content-center ">
    <label for="staticEmail" class="col-sm-2 col-form-label">Email</label>
    <div class="col-sm-4">
      <input type="email" class="form-control" id="exampleFormControlInput1" value={email} onChange={(e)=>setEmail(e.target.value)} placeholder="Enter Email"/>
    </div>
  </div>

  <div class="mb-3 row justify-content-center">
    <label for="inputPassword" class="col-sm-2 col-form-label">Password</label>
    <div class="col-sm-4">
      <input type="password" class="form-control" id="inputPassword" value={password} onChange={(e)=>setPassword(e.target.value)} placeholder="Enter password"/>
    </div>
    </div>


  <div class="mb-3 row justify-content-center">
    <label for="inputPassword" class="col-sm-2 col-form-label">Confirm Password</label>
    <div class="col-sm-4">
      <input type="password" class="form-control" id="inputPassword" value={confirmPassword} onChange={(e)=>setCpassword(e.target.value)} placeholder="Enter password again"/>

    </div>
  </div>

 <button type="button" id="btn1" class="btn btn-primary justify-content-center" onClick={handleStore}>Signup</button>
 </div>

  </>
  );
}

export default Signup;
