import React,{useState,useEffect} from 'react';





function Main() {
	const [moviedata,setMoviedata]=useState([]);

	const url="https://api.themoviedb.org/3/movie/upcoming?api_key=a0d1ccffb7077d4acb8d946b535565ac&language=en-US&page=1";

	useEffect(() => {
    loadData();
  },[]);

  const loadData=async()=>{
	  const response=await fetch(url);
	  const data=await response.json();
	  setMoviedata((data.results));
	  console.log(data);
  }

  return (
  <>

	{moviedata.map((mdata,id)=>{
		/*let text1="https://davistheater.com/movies/sing-2";
		let image1=text1.concat(mdata.poster_path);*/
		return(
		<>
	<div className="container main" >
		 <div className="card " >
		 {mdata.id===610253 &&  <img key={mdata.id} src="https://upload.wikimedia.org/wikipedia/en/c/c5/Clifford2021Poster.jpg" class="card-img-top" alt="not laoding..." height="200"/>}

			  <div className="card-body">
				  <h5 className="card-title" key={mdata.id}>{mdata.original_title}</h5>
				  <h4 className="card-text" key={mdata.id}>{mdata.vote_average}</h4>
				  <p className="card-text" key={mdata.id}>{mdata.overview} </p>
			  </div>
		 </div>
	</div>
      </>

	);
	})}

 </>
  );
}

export default Main;
