import React,{useState,useEffect} from 'react';
import Dashboard from "./Dashboard";
import Kyc from "./Kyc";


function Signup() {
	const[username,setUsername]=useState("test123");
	const[email,setEmail]=useState("test@gmail.com");
	const[password,setPassword]=useState("12345");
	const[val,setVal]=useState("");

	useEffect(()=>{                                                   //use of localstorage
		localStorage.setItem('username',JSON.stringify(username))
	},[username]);

	useEffect(()=>{                                                   //use of localstorage
		localStorage.setItem('email',JSON.stringify(email))
	},[email]);

	useEffect(()=>{                                                   //use of localstorage
		localStorage.setItem('password',JSON.stringify(password))
	},[password]);



  return (
  <>
  <div className="container mt-5 border border-primary bg-light pb-5 px-5">
  <h1 className="text-center">SIGNUP FORM</h1>
  <div className="group">
  <div class="mb-3 row ">
    <label for="staticEmail" class="col-sm-2 col-form-label">Username</label>
    <div class="col-sm-5 text-center">
      <input type="text"  class="form-control" id="staticEmail" value={username} required />
    </div>
  </div>
  <div class="mb-3 row">
    <label for="staticEmail" class="col-sm-2 col-form-label">Email</label>
    <div class="col-sm-5">
      <input type="email" class="form-control" id="staticEmail" value={email} required />
    </div>
  </div>
  <div class="mb-3 row">
    <label for="inputPassword" class="col-sm-2 col-form-label">Password</label>
    <div class="col-sm-5">
      <input type="password" class="form-control" id="inputPassword" value={password} required />
    </div>
  </div>
  </div>
  <div className="text-center">
  <button type="button" class="btn btn-primary btn-block" onClick={()=>setVal("true")}>LOGIN</button>
  </div>
  </div>

  {val && <Dashboard/>}  
  </>
  );
}

export default Signup;
