import React,{Component} from 'react';

class Api extends Component{
	constructor(props){
		super(props);
		this.state={
			items:[],
			isloaded:false,
		}
	}
	componentDidMount(){
		fetch("https://jsonplaceholder.typicode.com/users")
		.then(res=>res.json())
		.then(json=>{
			this.setState({
				isloaded:true,
				items:json,
			})
	});
	}
	render(){
		var {isloaded,items}=this.state;
		if(!isloaded){
			return <div>Loading....</div>;
		}
		else{
		return(
		<div>
		<ul>
		{items.map(item=>(
		<li key={item.id}>
		name:{item.name}|email:{item.email}
		</li>
		))};
		</ul>
		</div>
		);
	}
};
	}
export default Api;
