import React,{useState,useEffect} from 'react';
import Data from './Data';
import DataF from './DataF';
import DataS from './DataS';
import './Main_css.css';



function Main() {
	const [items,setItems]=useState(Data);
	const [flipcheck,setFlipcheck]=useState(false);
	const [shoecheck,setShoecheck]=useState(false);
	const [allcheck,setAllcheck]=useState(true);

	/*SHOE SIZE FUNCTION */

	const filteritem=(size1,name)=>{
		console.log(size1);
	let updateditems1=" ";
	updateditems1=Data.filter((elem1)=>{
		if(elem1.size===size1 && name==="btn1" ){
	return elem1.size===size1 ;
		}
		if(elem1.size===size1 && name==="btn2"){
	return elem1.size===size1 ;
		}
		if(elem1.size===size1 && name==="btn3"){
	return elem1.size===size1 ;
		}
		if(elem1.size===size1 && name==="btn4"){
	return elem1.size===size1 ;
		}
		if(elem1.size===size1 && name==="btn5"){
	return elem1.size===size1 ;
		}
		if(elem1.size===size1 && name==="btn6"){
	return elem1.size===size1 ;
		}
		if(elem1.size===size1 && name==="btn7"){
	return elem1.size===size1 ;
		}
		if(elem1.size===size1 && name==="btn8"){
	return elem1.size===size1 ;
		}




	});
	setItems(updateditems1);
	updateditems1=" ";

	};



	/*  CATEGORY FUNCTION */
	const checkitem2=(categ)=>{
	const updateditems=DataS.filter((elem)=>{
	return elem.category===categ;
	});
	setItems(updateditems);
	setFlipcheck(false);
	};

	const checkitem1=(categ)=>{
	const updateditems=DataF.filter((elem)=>{
	return elem.category===categ;
	});
	setItems(updateditems);
	setShoecheck(false);
	};

return (
  <>
  <div className="row">		
	<div className="column left">
	 <h1>CATEGORIES</h1>
	  <div className="check">
			 <input type="checkbox"  checked={flipcheck} onChange={(e)=>setFlipcheck(e.target.checked)} onClick={()=>checkitem1("flipflop")}/> Flip Flops <br/>
			 <input type="checkbox"  checked={shoecheck} onChange={(e)=>setShoecheck(e.target.checked)} onClick={()=>checkitem2("laceup")}/> Lace-Up shoes <br/>

	 </div>
	 <hr/>
	 <h1>SIZE</h1>
		 <div className="gridcontainer">
		  <button type="button" onClick={()=>filteritem(34,btn1)} >34</button>
		  <button type="button" onClick={()=>filteritem(35,btn2)}>35</button>
		  <button type="button" onClick={()=>filteritem(36,btn3)}>36</button>
		  <button type="button" onClick={()=>filteritem(37,btn4)}>37</button> <br/><br/>
		  <button type="button" onClick={()=>filteritem(38,btn5)}>38</button>
		  <button type="button" onClick={()=>filteritem(39,btn6)}>39</button>
		  <button type="button" onClick={()=>filteritem(40,btn7)}>40</button>
		  <button type="button" onClick={()=>filteritem(41,btn8)}>41</button>
		 </div>
	 <hr/>
	<h1>Price Range</h1>
	<input type="range" id="vol" name="vol" min="500" max="2000"/>

	</div>

	<div className="column right">
  {items.map((elem=>{

	  return(
	  <>
				<div className="grid-container">

					<img key={elem.id} src={elem.img}/>
					<h5 key={elem.id}>Price:- {elem.price}</h5>
					<h5 key={elem.id}>Size:-{elem.size}</h5>

				</div>  		
	</>	
);
  }))}

  </div>
  </div>
  </>
  );
}

export default Main;
