import React,{useState,useEffect} from 'react';
import axios from 'axios';
import './Task_css.css';


function Main() {
	 const [lyricsdata,setLyricsdata]=useState([]);
	 const [searchterm,setSearchterm]=useState("");


	useEffect(() => {
    const fetchData = async () => {
      const result = await axios(`https://cors-access-allow.herokuapp.com/https://api.musixmatch.com/ws/1.1/chart.tracks.get?chart_name=top&page=1&page_size=10&country=nl&f_has_lyrics=1&apikey=${process.env.REACT_APP_MM_KEY}`);
      console.log(result);
	  setLyricsdata(result.data.message.body.track_list);
    };

    fetchData();
  },[]);


  return (
  <>
	  <div className="container header">
	  <input 
				type="text"
                id="text1"				
				placeholder="SEARCH LYRICS"
				value={searchterm}
				onChange={(event)=>{setSearchterm(event.target.value)}}/>

	  </div>

	<div className="container header1">
	{lyricsdata.map((item=>{
		console.log(lyricsdata);
		return(
	<>
	<h1 key={item.lyrics_id}>{item.lyrics_body}</h1>

	</>
	);
	}))}
	</div>
  </>
  );
}

export default Main;
