import React,{useState} from 'react';
import Data from './Data';
import './Main_css.css';

function Main() {
	const[items,setItems]= useState(Data);
	const [flipcheck,setFlip]=useState(false);
	const [shoecheck,setShoe]=useState(false);
	const [allcheck,setAll]=useState(True);

  return (
  <>
  <div className="row">
	<div className="column left">
	 <h1>CATEGORIES</h1>
	  <div className="check">
			 <input type="checkbox" onClick={()=>setFlip(true)} /> Flip Flops <br/>
			 <input type="checkbox" /> Sneakers   <br/>
			 <input type="checkbox" onClick={()=>setShoe(true)} /> Lace-Up shoes <br/>
			 <input type="checkbox" /> Shoe Accessories <br/>
	 </div>
	 <hr/>
	 <h1>SIZE</h1>
		 <div className="gridcontainer">
		  <button type="button">34</button>
		  <button type="button">35</button>
		  <button type="button">36</button>
		  <button type="button">37</button> <br/><br/>
		  <button type="button">38</button>
		  <button type="button">39</button>
		  <button type="button">40</button>
		  <button type="button">41</button>
		 </div>
	 <hr/>
	<h1>Price Range</h1>
	<input type="range" id="vol" name="vol" min="500" max="2000"/>

	</div>

	<div className="column right">
  {items.map((elem=>{
	  return(
	  <>
	  { allcheck &&
	  <div className="grid-container">

			<img key={elem.id} src={elem.img}/>
			<h3 key={elem.id}>Price:- {elem.price}</h3>

		</div>
	  }
	  {flipcheck & elem.category=="flipflop" && 

		<div className="grid-container">

			<img key={elem.id} src={elem.img}/>
			<h3 key={elem.id}>Price:- {elem.price}</h3>

		</div>
	  }
	  {shoecheck & elem.category=="laceup" && 

		<div className="grid-container">

			<img key={elem.id} src={elem.img}/>
			<h3 key={elem.id}>Price:- {elem.price}</h3>

		</div>
	  }






	</>
  );
  }))}

  </div>
  </div>
  </>
  );
}

export default Main;
