import React from 'react';

class Kyc extends React.Component {
  constructor(props) {
    super(props);
  }
  componentDidMount() {    
    this.loadBlockpassWidget()
  }

  loadBlockpassWidget = () => {
    const blockpass = new window.BlockpassKYCConnect(
      'test@gmail.com', // service client_id from the admin console
      {
        refId: "test@gmail.com", // assign the local user_id of the connected user
      }
    )

    blockpass.startKYCConnect()

    blockpass.on('KYCConnectSuccess', () => {
      //add code that will trigger when data have been sent.
	  console.log("KYC connect is successful!!");
    })
  }

  render() {
    return (
	<>
	<div className="container bg-primary pb-5">
	<h1 className="text-center">KYC Connect</h1>
	<button id="blockpass-kyc-connect" style={{marginLeft:"490px"}}>
        Verify with Blockpass
      </button>
	 </div> 
	</> 
	);
  }
}

export default Kyc;
