import React,{useState} from 'react';
import { Link } from 'react-router-dom';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import './Main_c.css';
import Header from './Header.js';
import Freeapp from './Freeapp';


function Main(){
	const[freeapp,setFreeapp]=useState(false);
	return(
	<>

	<header className="head1">
	<h1>NETFLIX</h1>
	<Router>
	<div className="l1" >
	<Link to='/signin' exact target="_blank" className="btn btn-primary" >SIGN IN</Link>
	</div>
	<div>

		      <Route exact path="/signin" component={Header}/>


	</div>
    </Router>
	</header>
	<div className="main1">
	<h1>The Netflix app is the best way to watch on your smartphone or tablet.</h1>
	<button type="button" class="btn bg-primary" onClick={()=>setFreeapp(true)}>Get the free App</button>
		{freeapp?<Freeapp/>:null}
	</div>
	</>
	);
}

export default Main;
